import React, { useState, useEffect } from 'react';
import { Container, Card, Form, Button, Table } from 'react-bootstrap';
import api from '../../axiosConfig';

const Sales = () => {
  const [sales, setSales] = useState([]);
  const [formData, setFormData] = useState({
    customer_id: '',
    inventory_id: '',
    discount: '',
    payment_method: 'cash',
  });

  useEffect(() => {
    fetchSales();
  }, []);

  const fetchSales = async () => {
    try {
      const response = await api.get('/sale');
      setSales(response.data);
    } catch (error) {
      console.error('Error fetching sales:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/sale', formData);
      fetchSales();
    } catch (error) {
      console.error('Error completing sale:', error);
    }
  };

  return (
    <Container>
      <Card className="p-4 mb-4">
        <Card.Title>Sales Management</Card.Title>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formCustomerId">
              <Form.Label>Customer ID</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter customer ID"
                name="customer_id"
                value={formData.customer_id}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formInventoryId">
              <Form.Label>Inventory ID</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter inventory ID"
                name="inventory_id"
                value={formData.inventory_id}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formDiscount">
              <Form.Label>Discount</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter discount (if any)"
                name="discount"
                value={formData.discount}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formPaymentMethod">
              <Form.Label>Payment Method</Form.Label>
              <Form.Control
                as="select"
                name="payment_method"
                value={formData.payment_method}
                onChange={handleChange}
              >
                <option value="cash">Cash</option>
                <option value="card">Card</option>
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">
              Complete Sale
            </Button>
          </Form>
        </Card.Body>
      </Card>

      <Card>
        <Card.Body>
          <Card.Title>Sales History</Card.Title>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Customer ID</th>
                <th>Inventory ID</th>
                <th>Discount</th>
                <th>Payment Method</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {sales.map(sale => (
                <tr key={sale.id}>
                  <td>{sale.customer_id}</td>
                  <td>{sale.inventory_id}</td>
                  <td>{sale.discount}</td>
                  <td>{sale.payment_method}</td>
                  <td>{new Date(sale.sale_date).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Sales;
