import React, { useState, useEffect } from 'react';
import { Container, Card, Form, Button, Table } from 'react-bootstrap';
import api from '../../axiosConfig';

const Sales = () => {
  const [sales, setSales] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    customer_id: '',
    inventory_id: '',
    discount: '',
    payment_method: 'cash',
  });

  useEffect(() => {
    fetchSales();
    fetchCustomers();
    fetchInventory();
  }, []);

  const fetchSales = async () => {
    try {
      const response = await api.get('/sale');
      setSales(response.data);
    } catch (error) {
      console.error('Error fetching sales:', error);
    }
  };

  const fetchCustomers = async () => {
    try {
      const response = await api.get('/customer');
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const fetchInventory = async () => {
    try {
      const response = await api.get('/inventory');
      setInventory(response.data);
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/sale', formData);
      fetchSales();
    } catch (error) {
      console.error('Error completing sale:', error);
    }
  };

  return (
    <Container>
      <Card className="p-4 mb-4">
        <Card.Title>Sales Management</Card.Title>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formCustomerId">
              <Form.Label>Customer</Form.Label>
              <Form.Control
                as="select"
                name="customer_id"
                value={formData.customer_id}
                onChange={handleChange}
              >
                <option value="">Select a customer</option>
                {customers.map(customer => (
                  <option key={customer.id} value={customer.id}>
                    {customer.name}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="formInventoryId">
              <Form.Label>Inventory</Form.Label>
              <Form.Control
                as="select"
                name="inventory_id"
                value={formData.inventory_id}
                onChange={handleChange}
              >
                <option value="">Select an inventory item</option>
                {inventory.map(item => (
                  <option key={item.id} value={item.id}>
                    {item.name}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="formDiscount">
              <Form.Label>Discount</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter discount (if any)"
                name="discount"
                value={formData.discount}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formPaymentMethod">
              <Form.Label>Payment Method</Form.Label>
              <Form.Control
                as="select"
                name="payment_method"
                value={formData.payment_method}
                onChange={handleChange}
              >
                <option value="cash">Cash</option>
                <option value="card">Card</option>
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">
              Complete Sale
            </Button>
          </Form>
        </Card.Body>
      </Card>

      <Card>
        <Card.Body>
          <Card.Title>Sales History</Card.Title>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Customer</th>
                <th>Inventory</th>
                <th>Discount</th>
                <th>Payment Method</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {sales.map(sale => (
                <tr key={sale.id}>
                  <td>{customers.find(c => c.id === sale.customer_id)?.name || 'Unknown'}</td>
                  <td>{inventory.find(i => i.id === sale.inventory_id)?.name || 'Unknown'}</td>
                  <td>{sale.discount}</td>
                  <td>{sale.payment_method}</td>
                  <td>{new Date(sale.sale_date).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Sales;
