import React, { useState, useEffect } from 'react';
import { Container, Card, Form, Button, Table } from 'react-bootstrap';
import Select from 'react-select';
import api from '../../axiosConfig';

const Sales = () => {
  const [sales, setSales] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    customer_id: '',
    inventory_id: '',
    discount: '',
    payment_method: 'cash',
    selling_price: ''
  });

  useEffect(() => {
    fetchSales();
    fetchCustomers();
    fetchInventory();
  }, []);

  const fetchSales = async () => {
    try {
      const response = await api.get('/sale');
      setSales(response.data);
    } catch (error) {
      console.error('Error fetching sales:', error);
    }
  };

  const fetchCustomers = async () => {
    try {
      const response = await api.get('/customer');
      setCustomers(response.data.map(customer => ({
        value: customer.id,
        label: customer.name
      })));
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const fetchInventory = async () => {
    try {
      const response = await api.get('/inventory');
      setInventory(response.data.map(item => ({
        value: item.id,
        label: item.name
      })));
    } catch (error) {
      console.error('Error fetching inventory:', error);
    }
  };

  const handleSelectChange = (selectedOption, { name }) => {
    setFormData({
      ...formData,
      [name]: selectedOption ? selectedOption.value : ''
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/sale', formData);
      fetchSales();
      window.open(response.data.invoice_url, '_blank'); // Open the invoice in a new window
    } catch (error) {
      console.error('Error completing sale:', error);
    }
  };

  const handleInvoice = (url) => {
    window.open(url, '_blank'); // Open the invoice in a new window
  };

  return (
    <Container>
      <Card className="p-4 mb-4">
        <Card.Title>Sales Management</Card.Title>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formCustomerId">
              <Form.Label>Customer</Form.Label>
              <Select
                name="customer_id"
                options={customers}
                value={customers.find(option => option.value === formData.customer_id)}
                onChange={handleSelectChange}
                placeholder="Select a customer"
              />
            </Form.Group>

            <Form.Group controlId="formInventoryId">
              <Form.Label>Inventory</Form.Label>
              <Select
                name="inventory_id"
                options={inventory}
                value={inventory.find(option => option.value === formData.inventory_id)}
                onChange={handleSelectChange}
                placeholder="Select an inventory item"
              />
            </Form.Group>

            <Form.Group controlId="formSellingPrice">
              <Form.Label>Selling Price</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter selling price"
                name="selling_price"
                value={formData.selling_price}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formDiscount">
              <Form.Label>Discount</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter discount (if any)"
                name="discount"
                value={formData.discount}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formPaymentMethod">
              <Form.Label>Payment Method</Form.Label>
              <Form.Control
                as="select"
                name="payment_method"
                value={formData.payment_method}
                onChange={handleChange}
              >
                <option value="cash">Cash</option>
                <option value="card">Card</option>
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">
              Complete Sale
            </Button>
          </Form>
        </Card.Body>
      </Card>

      <Card>
        <Card.Body>
          <Card.Title>Sales History</Card.Title>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Customer</th>
                <th>Inventory</th>
                <th>Selling Price</th>
                <th>Discount</th>
                <th>Payment Method</th>
                <th>Date</th>
                <th>Invoice</th>
              </tr>
            </thead>
            <tbody>
              {sales.map(sale => (
                <tr key={sale.id}>
                  <td>{sale.customer_name}</td>
                  <td>{sale.item_name}</td>
                  <td>${sale.selling_price}</td>
                  <td>${sale.discount}</td>
                  <td>{sale.payment_method}</td>
                  <td>{new Date(sale.sale_date).toLocaleString()}</td>
                  <td>
                    <Button variant="info" onClick={() => handleInvoice(`/sale/invoice/${sale.id}`)}>
                      View Invoice
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Sales;
